\">package main\n\nimport (\n	&quot;context&quot;\n	&quot;fmt&quot;\n	&quot;go.etcd.io/etcd/clientv3&quot;\n	&quot;time&quot;\n)\n\nfunc main() {\n	cli, err := clientv3.New(clientv3.Config {\n		Endpoints: []string{&quot;127.0.0.1:2379&quot;}, // etcd的节点，可以传入多个\n		DialTimeout: 5*time.Second, // 连接超时时间\n	})\n\n	if err != nil {\n		fmt.Printf(&quot;connect to etcd failed, err: %v \\n&quot;, err)\n		return\n	}\n	fmt.Println(&quot;connect to etcd success&quot;)\n\n	// 延迟关闭\n	defer cli.Close()\n\n	// put操作  设置1秒超时\n	ctx, cancel := context.WithTimeout(context.Background(), time.Second)\n	_, err = cli.Put(ctx, &quot;moxi&quot;, &quot;lalala&quot;)\n	cancel()\n	if err != nil {\n		fmt.Printf(&quot;put to etcd failed, err:%v \\n&quot;, err)\n		return\n	}\n\n	// get操作，设置1秒超时\n	ctx, cancel = context.WithTimeout(context.Background(), time.Second)\n	resp, err := cli.Get(ctx, &quot;q1mi&quot;)\n	cancel()\n	if err != nil {\n		fmt.Printf(&quot;get from etcd failed, err:%v \\n&quot;, err)\n		return\n	}\n	fmt.Println(resp)\n}\n</code></pre>\n, tagUid=5626932d452c2ad863d9b3cb0b69d22d, blogSortUid=a03d7290b1c04b6eaf46659661b47032, fileUid=74fdc97330f33eff09bea0cabdfb8c01, adminUid=1f01cd1d2f474743b241d74008b12333, isPublish=1, isOriginal=1, author=陌溪, articlesPart=蘑菇博客, level=0, type=0, outsideLink=null, tagList=[Tag(content=学习笔记, clickCount=217, sort=216)], photoList=[http://82.156.177.91:8600/blog/admin/png/2021/10/25/1635174297380.png], blogSort=BlogSort(sortName=后端开发, content=后端开发专题, clickCount=372, sort=6, icon=el-icon-monitor), praiseCount=null, copyright=null, levelKeyword=null, useSort=0, sort=0, openComment=1, orderByDescColumn=null, orderByAscColumn=null, userUid=null, articleSource=0)}','编辑博客',1,'2021-10-25 23:09:45','2021-10-25 23:09:45','中国|浙江省|杭州市|移动',79),('ae308ccfc0c33f7f4cec75c370940ef3','admin','1f01cd1d2f474743b241d74008b12333','39.182.27.13','/user/edit','POST','com.moxi.mogublog.admin.restapi.UserRestApi','edit','{\"result\":org.springframework.validation.BeanProperty