(27,'mogu-admin.yaml','dev','#app\r\nserver:\r\n  port: 8601\r\n\r\n#阿里大于\r\ntemplateCode: SMS_XXXXXX #短信模板编号\r\nsignName: 蘑菇博客\r\n#项目名称\r\nPROJECT_NAME: 蘑菇博客\r\n\r\nfile:\r\n  upload:\r\n    path: D:/mogu_blog/data/\r\n\r\n# 邮箱验证\r\nmoguBlog:\r\n  email: mogublog@163.com\r\n\r\n# 蘑菇博客登录默认密码\r\nDEFAULE_PWD: mogu2018\r\n\r\n#博客相关配置\r\nBLOG:\r\n  HOT_COUNT: 5 #热门博客数量\r\n  NEW_COUNT: 15 #最新博客数据\r\n  FIRST_COUNT: 5 #一级推荐\r\n  SECOND_COUNT: 2 #二级推荐\r\n  THIRD_COUNT: 3 #三级推荐\r\n  FOURTH_COUNT: 5 #四级推荐\r\n\r\n#spring\r\nspring:\r\n  jackson:\r\n    serialization:\r\n      INDENT_OUTPUT: true\r\n    date-format: yyyy-MM-dd HH:mm:ss\r\n    time-zone: Asia/Shanghai\r\n  jmx:\r\n    default-domain: mogu_admin\r\n  thymeleaf:\r\n    cache: true   #关闭缓存\r\n  application:\r\n    name: mogu-admin\r\n  security:\r\n    user:\r\n      name: user\r\n      password: password123\r\n\r\n  # sleuth 配置\r\n  sleuth:\r\n    web:\r\n      client:\r\n        enabled: true\r\n    sampler:\r\n      probability: 1.0 # 采样比例为: 0.1(即10%),设置的值介于0.0到1.0之间，1.0则表示全部采集。\r\n  # zipkin 配置\r\n  zipkin:\r\n    base-url: http://localhost:9411  # 指定了Zipkin服务器的地址\r\n\r\n  # DATABASE CONFIG\r\n  datasource:\r\n    username: root\r\n    password: root\r\n    url: jdbc:mysql://localhost:3306/mogu_blog?useUnicode=true&characterEncoding=utf-8&zeroDateTimeBehavior=convertToNull&transformedBitIsBoolean=true&useSSL=false&serverTimezone=Asia/Shanghai\r\n    driver-class-name: com.mysql.cj.jdbc.Driver\r\n    type: com.alibaba.druid.pool.DruidDataSource\r\n\r\n    # 初始化大小，最小，最大\r\n    initialSize: 20\r\n    minIdle: 5\r\n    maxActive: 200\r\n\r\n    #连接等待超时时间\r\n    maxWait: 60000\r\n\r\n    #配置隔多久进行一次检测(检测可以关闭的空闲连接)\r\n    timeBetweenEvictionRunsMillis: 60000\r\n\r\n    #配置连接在池中的最小生存时间\r\n    minEvictableIdleTimeMillis: 300000\r\n\r\n    validationQuery: SELECT 1 FROM DUAL\r\n\r\n    dbcp:\r\n      remove-abandoned: true\r\n      #泄露的连接可以被删除的超时时间（秒），该值应设置为应用程序查询可能执行的最长时间\r\n      remove-abandoned-timeout: 180\r\n\r\n  