(32,'mogu-spider.yaml','dev','#app\r\nserver:\r\n  port: 8608\r\n\r\n#spring\r\nspring:\r\n  jmx:\r\n    default-domain: mogu_spider\r\n  thymeleaf:\r\n    cache: true   #关闭缓存\r\n  application:\r\n    name: mogu-spider\r\n  security:\r\n    user:\r\n      name: user\r\n      password: password123\r\n  boot:\r\n    admin:\r\n      client:\r\n        enabled: true\r\n        url: http://localhost:8606\r\n        username: user\r\n        password: password123\r\n        instance:\r\n          service-base-url: http://localhost:8608\r\n\r\n  #redis\r\n  redis:\r\n    host: 127.0.0.1 #redis的主机ip\r\n    port: 6379\r\n\r\n  # sleuth 配置\r\n  sleuth:\r\n    web:\r\n      client:\r\n        enabled: true\r\n    sampler:\r\n      probability: 1.0 # 采样比例为: 0.1(即10%),设置的值介于0.0到1.0之间，1.0则表示全部采集。\r\n  # zipkin 配置\r\n  zipkin:\r\n    base-url: http://localhost:9411  # 指定了Zipkin服务器的地址\r\n\r\n  # DATABASE CONFIG\r\n  datasource:\r\n    username: root\r\n    password: root\r\n    url: jdbc:mysql://localhost:3306/mogu_blog?useUnicode=true&characterEncoding=utf-8&zeroDateTimeBehavior=convertToNull&transformedBitIsBoolean=true&useSSL=false&serverTimezone=GMT%2B8\r\n    driver-class-name: com.mysql.cj.jdbc.Driver\r\n    type: com.alibaba.druid.pool.DruidDataSource\r\n\r\n    initialSize: 5\r\n    minIdle: 5\r\n    maxActive: 20\r\n    maxWait: 60000\r\n    timeBetweenEvictionRunsMillis: 60000\r\n    minEvictableIdleTimeMillis: 300000\r\n    testWhileIdle: true\r\n    testOnBorrow: false\r\n    testOnReturn: false\r\n    poolPreparedStatements: true\r\n\r\n    #配置监控统计拦截的filters，去掉后监控界面sql无法统计，\'wall\'用于防火墙\r\n    filters: stat,wall,log4j\r\n    maxPoolPreparedStatementPerConnectionSize: 20\r\n    useGlobalDataSourceStat: true\r\n    connectionProperties: druid.stat.mergeSql=true;druid.stat.slowSqlMillis=500\r\n\r\n#eureka:\r\n#  client:\r\n#    healthcheck:\r\n#      enabled: true\r\n#    registerWithEureka: true\r\n#    fetchRegistry: true\r\n#    serviceUrl:\r\n#      defaultZone: http: