ress}:${spring.application.instance_id:${server.port}}\r\n#    lease-renewal-interval-in-seconds: 5\r\n\r\n  #nacos相关配置\r\n  cloud:\r\n    nacos:\r\n      discovery:\r\n        server-addr: localhost:8848\r\n\r\n# 或者：\r\nfeign.hystrix.enabled: false # 索性禁用feign的hystrix支持\r\n# 设置feign调用超时时间\r\nribbon:\r\n  ReadTimeout: 20000\r\n  ConnectTimeout: 20000\r\n\r\nmanagement:\r\n  endpoints:\r\n    web:\r\n      exposure:\r\n        include: \"*\"\r\n  endpoint:\r\n    health:\r\n      show-details: always\r\n\r\n#mybatis\r\nmybatis-plus:\r\n  mapper-locations: classpath:/mapper/*Mapper.xml\r\n  #实体扫描，多个package用逗号或者分号分隔\r\n  typeAliasesPackage: com.moxi.mogublog.commons.entity\r\n  global-config:\r\n    # 数据库相关配置\r\n    db-config:\r\n      #主键类型  0:\"数据库ID自增\", 1:\"用户输入ID\",2:\"全局唯一ID (数字类型唯一ID)\", 3:\"全局唯一ID UUID\";\r\n      id-type: UUID\r\n      #字段策略 IGNORED:\"忽略判断\",NOT_NULL:\"非 NULL 判断\"),NOT_EMPTY:\"非空判断\"\r\n      field-strategy: NOT_EMPTY\r\n      #驼峰下划线转换\r\n      column-underline: true\r\n      #数据库大写下划线转换\r\n      #capital-mode: true\r\n      #逻辑删除配置\r\n      logic-delete-value: 0\r\n      logic-not-delete-value: 1\r\n      db-type: mysql\r\n    #刷新mapper 调试神器\r\n    refresh: true\r\n  # 原生配置\r\n  configuration:\r\n    map-underscore-to-camel-case: true\r\n    cache-enabled: false\r\n\r\n# 第三方登录，参考 http://moguit.cn/#/info?blogUid=fe9e352eb95205a08288f21ec3cc69e0\r\njustAuth:\r\n  clientId:\r\n    gitee: ec2dba332701caa209935512c69fc7962530b8619acd9b6cb03106a7c13c1c5e # 改成自己的\r\n    github: c99bfe31f8774ec8e242 # 改成自己的\r\n    qq: XXXXXXXXXXXXXXX  # 改成自己的\r\n  clientSecret:\r\n    gitee: 993930de16b61b8146f7d30c693afd328b4d42116cf2da748f275077e4ef5e47 # 改成自己的\r\n    github: ec088d14ab8076e2beed58fcb95e37a0fc586618 # 改成自己的\r\n    qq: XXXXXXXXXXXXXXXXXX # 改成自己的','233689198acc543aa89350d3f23058a2','2020-06-20 09:56:24','2020-06-20 09:56:24',NULL,'0:0:0:0:0:0:0:1','','f431bd53-e6c6-451c-803c-b32712f38562',NULL,NULL,NULL,'yaml',NULL,NULL),
