OM DUAL\n\n    dbcp:\n      remove-abandoned: true\n      #泄露的连接可以被删除的超时时间（秒），该值应设置为应用程序查询可能执行的最长时间\n      remove-abandoned-timeout: 180\n\n    testWhileIdle: true\n    testOnBorrow: false\n    testOnReturn: false\n    poolPreparedStatements: true\n\n    #配置监控统计拦截的filters，去掉后监控界面sql无法统计，\'wall\'用于防火墙\n    filters: stat,wall,log4j\n    maxPoolPreparedStatementPerConnectionSize: 20\n    useGlobalDataSourceStat: true\n    connectionProperties: druid.stat.mergeSql=true;druid.stat.slowSqlMillis=500\n\n  #redis\n  redis:\n    host: redis #redis的主机ip\n    port: 6379\n    password: mogu2018\n\n  rabbitmq:\n    host: rabbitmq #rabbitmq的主机ip\n    port: 5672\n    username: admin\n    password: mogu2018\n\n  boot:\n    admin:\n      client:\n        enabled: true\n        url: http://mogu_monitor:8606\n        username: user\n        password: password123\n        instance:\n          service-base-url: http://mogu_admin:8601\n\n  # sleuth 配置\n  sleuth:\n    web:\n      client:\n        enabled: true\n    sampler:\n      probability: 1.0 # 采样比例为: 0.1(即10%),设置的值介于0.0到1.0之间，1.0则表示全部采集。\n  # zipkin 配置\n  zipkin:\n    base-url: http://zipkin:9411  # 指定了Zipkin服务器的地址\n    \n# 禁用feign的hystrix支持\nfeign.hystrix.enabled: false\n\n#ribbon的超时时间 30秒\nribbon:\n  ReadTimeout: 30000\n  ConnectTimeout: 30000\n\nmanagement:\n  endpoints:\n    web:\n      exposure:\n        include: \"*\"\n  endpoint:\n    health:\n      show-details: always\n\n#mybatis\nmybatis-plus:\n  mapper-locations: classpath:/mapper/*Mapper.xml\n  #实体扫描，多个package用逗号或者分号分隔\n  typeAliasesPackage: com.moxi.mogublog.commons.entity\n  global-config:\n    # 数据库相关配置\n    db-config:\n      #主键类型  0:\"数据库ID自增\", 1:\"用户输入ID\",2:\"全局唯一ID (数字类型唯一ID)\", 3:\"全局唯一ID UUID\";\n      id-type: UUID\n      #字段策略 IGNORED:\"忽略判断\",NOT_NULL:\"非 NULL 判断\"),NOT_EMPTY:\"非空判断\"\n      field-strategy: NOT_EMPTY\n      #驼峰下划线转换\n      column-underline: true\n      #数据库大写下划线转换\n      #capital-mode: true\n      #逻辑删除配置\n      logic-delete-value: 0\n     