(137,'mogu-gateway-test.yaml','test','server:\n  address: \":9607\"\n  dumpRouterMap: true\n  routeOverWrite: true\n  serverRoot: \"resource/public\"\n  openapiPath: \"/v3/api-docs\"\n  swaggerPath: \"/swagger\"\n  NameToUriType: 3\n  maxHeaderBytes: \"20KB\"\n  clientMaxBodySize: \"50MB\"\n  # Logging配置\n  logPath: \"resource/log/server\"                 # 日志文件存储目录路径，建议使用绝对路径。默认为空，表示关闭\n  logStdout: true               # 日志是否输出到终端。默认为true\n  errorStack: true               # 当Server捕获到异常时是否记录堆栈信息到日志中。默认为true\n  errorLogEnabled: true               # 是否记录异常日志信息到日志中。默认为true\n  errorLogPattern: \"error-{Ymd}.log\"  # 异常错误日志文件格式。默认为\"error-{Ymd}.log\"\n  accessLogEnabled: true              # 是否记录访问日志。默认为false\n  accessLogPattern: \"access-{Ymd}.log\" # 访问日志文件格式。默认为\"access-{Ymd}.log\"\n\n\nlogger:\n  path: \"resource/log/server\"   # 日志文件路径。默认为空，表示关闭，仅输出到终端\n  file: \"{Y-m-d}.log\" # 日志文件格式。默认为\"{Y-m-d}.log\"\n  prefix: \"\"            # 日志内容输出前缀。默认为空\n  level: \"all\"         # 日志输出级别\n  ctxKeys: [ ]            # 自定义Context上下文变量名称，自动打印Context的变量到日志中。默认为空\n  header: true          # 是否打印日志的头信息。默认true\n  stdout: true          # 日志是否同时输出到终端。默认true\n  rotateSize: 100MB             # 按照日志文件大小对文件进行滚动切分。默认为0，表示关闭滚动切分特性\n  rotateExpire: 7d             # 按照日志文件时间间隔对文件滚动切分。默认为0，表示关闭滚动切分特性\n  rotateBackupLimit: 2             # 按照切分的文件数量清理切分文件，当滚动切分特性开启时有效。默认为0，表示不备份，切分则删除\n  rotateBackupExpire: 3d             # 按照切分的文件有效期清理切分文件，当滚动切分特性开启时有效。默认为0，表示不备份，切分则删除\n  rotateBackupCompress: 9             # 滚动切分文件的压缩比（0-9）。默认为0，表示不压缩\n  rotateCheckInterval: \"1h\"          # 滚动切分的时间检测间隔，一般不需要设置。默认为1小时\n  stdoutColorDisabled: true         # 关闭终端的颜色打印。默认开启\n  writerColorEnable: true         # 日志文件是否带上颜色。默认false，表示不带颜色\n\nroutes:\n  # 采用自定义路由 ID（有固定用法，不同的 id 有不同的功能，详见：https://cloud.spring.io/spring-cloud-gateway/2.0.x/single/spring-cloud-gateway.html#gateway-route-filters）\n  - id: mogu_admin\n    # 采用 LoadBalanceClient 方式请求，以 lb:// 开头，后面的是注册在 Nacos 上的服务名\n    uri: lb://mogu-admin\n    # Predicate 翻译过来是“谓词”的意思，必须，主要作用是匹配用户的请求，有很多种用法\n    predicates:\n      # 路径匹配，以 api 开头，直接配置是不生效的，看 filters 配置\n      - Path=/mogu-admin/**\n    filters:\n      # 前缀过滤，默认配置下，我们的请求路径是 http://localhost:8888/business-oauth2/** 这时会路由到指定的服务\n      # 此处配置去掉 1 个路径前缀，再配置上面的 Path=/api/**，就能按照 http://localhost:8888/api/** 的方式访问了\n      - StripPrefix=1\n  - id: mogu_web\n    uri: lb://mogu-web\n    predicates:\n      - Path=/mogu-web/**\n    filters:\n      - StripPrefix=1\n  - id: mogu_search\n    uri: lb://mogu-search\n    predicates:\n      - Path=/mogu-search/**\n    filters:\n      - StripPrefix=1\n  - id: mogu_picture\n    uri: lb://mogu-picture\n    predicates:\n      - Path=/mogu-picture/**\n    filters:\n      - StripPrefix=1\n  - id: mogu_spider\n    uri: lb://mogu-spider\n    predicates:\n      - Path=/mogu-spider/**\n    filters:\n      - StripPrefix=1\n  - id: mogu_monitor\n    uri: lb://mogu-monitor\n    predicates:\n      - Path=/mogu-monitor/**\n    filters:\n      - StripPrefix=1\n\n','9ca34154aa00cef8c06328d8cb5a1d1a','2022-11-15 09:42:17','2022-11-15 09:42:17',NULL,'172.18.0.1','','','',NULL,NULL,'yaml',NULL,''),
